import React, { useState, useEffect } from "react";
// import { DISHES } from "../shared/dishesh";
// import { COMMENTS } from "../shared/comments";
// import { LEADERS } from "../shared/leaders";
// import { PROMOTIONS } from "../shared/promotions";
import HomeComponent from "./HomeComponent";
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent";
import MenuComponent from "./MenuComponent";
import AboutComponent from "./AboutComponent";
import { Switch, Route, Redirect, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import ContactComponent from "./ContactComponent";
import DishdetailComponent from "./DishdetailComponent";
import { addComment, fetchDishes } from "../redux/actionCreators";



const mapStateToProps = state => {
  return {
    dish : state.dish,
    comments : state.comments,
    promotions : state.promotions,
    leaders : state.leaders
  }

}


const mapDispatchToProps = (dispatch)=>({
  addComment:(dishId, rating, author, comment ) => dispatch(addComment(dishId, rating, author, comment )),
  fetchDishes : () => {dispatch(fetchDishes())}
})



const MainComponent = (props) => {
 
function componentDidMount(){
  props.fetchDishes();
}

componentDidMount();

  const Homepage = () => {
    return (
      <HomeComponent
        dish={props.dishes.dish.filter((dish) => dish.featured)[0]}
        promotions={props.promotions.filter((promo) => promo.featured)[0]}
        leaders={props.leaders.filter((leader) => leader.featured)[0]}
      />
    );
  };

  const DishWithId = ({match}) =>{
    // console.log(match.params.id);
    return (
      <DishdetailComponent dish={props.dish.filter((dish)=>dish.id===parseInt(match.params.id,10))[0]}
        comments={props.comments.filter((com)=>com.dishId===parseInt(match.params.id,10))}
        addComment={props.addComment}
      />
      // <DishdetailComponent dish={dish} comments={comments}/>
    )
  }

  const aboutPage = () =>{
    return(
      <AboutComponent leaders={props.leaders}/>
    )
  }
  return (
    <div>
      <HeaderComponent />
      <Switch>
        <Route path="/home" component={Homepage} />
        <Route exact path="/menu" component={() => <MenuComponent dishes={props.dish} />}/>
        <Route exact path="/contact" component={() => <ContactComponent />} />
        <Route path="/menu/:id" component={DishWithId} />
        <Route path="/about" component={aboutPage} />
        <Redirect to="/home" />
      </Switch>

      <FooterComponent />
    </div>
  );
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MainComponent));
