import React, { useState,useEffect } from 'react'
import DishdetailComponent from './DishdetailComponent';
import { Card, CardBody , CardText,CardImg, CardImgOverlay, CardTitle } from 'reactstrap'

const MenuComponent = (props) => {

const [selectedDish, setSelectedDish] = useState('');

function onDishSelect(dish){
    setSelectedDish(dish)
}

function renderDish(dish){
    if(!dish){
        return(<div></div>)
    }else{
        return(
            <DishdetailComponent dish={dish}/>
        )
    }
}

function imClicked(dishname){
    console.log(dishname)
}
   const menuItem = props.dishes.map((dish)=>{
        return(
            <div key={dish.id} className='col-12 col-md-5 m-1'>
            <button className='btn btn-sm btn-primary' onClick={()=>imClicked(dish.name)}>click me Mf</button>
                <Card onClick={()=>onDishSelect(dish)}>
                    <CardImg width="100%" src={dish.image} alt={dish.middle} />
                    <CardImgOverlay>
                        <CardTitle>{dish.name}</CardTitle>
                    </CardImgOverlay>
                </Card>
            </div>
        )
    }) 
  return (
    <div className='container'>
        <div className='row'>
            {menuItem}
         </div>
         <div className='row'>
             {renderDish(selectedDish)}
         </div>
    </div>
  )
}

export default MenuComponent
