import { useEffect, useState } from 'react';

import './App.css';
import MenuComponent from './components/MenuComponent';
import { DISHES } from './shared/dishesh'
function App() {
  const [dish, setdish] = useState([]);
  useEffect(()=>{

    setdish(DISHES);
  },[])
  console.log(dish)

  function showDishName(dishname){
    console.log(dishname);
  }
  return (
    <div className="App">

        <MenuComponent dishes={dish} onClick={(dishname)=>showDishName(dishname)}/>
    </div>
  );
}

export default App;
