import React, { useState, useEffect } from "react";

import HomeComponent from "./HomeComponent";
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent";
import MenuComponent from "./MenuComponent";
import AboutComponent from "./AboutComponent";
import { Switch, Route, Redirect, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import ContactComponent from "./ContactComponent";
import DishdetailComponent from "./DishdetailComponent";
import { addComment, fetchDishes } from "../redux/actionCreators";

const mapStateToProps = (state) => {
  return {
    dish: state.dish,
    comments: state.comments,
    promotions: state.promotions,
    leaders: state.leaders,
  };
};

const mapDispatchToProps = (dispatch) => ({
  addComment: (dishId, rating, author, comment) =>
    dispatch(addComment(dishId, rating, author, comment)),
  fetchDishes: () => {
    dispatch(fetchDishes());
  },
});

const MainComponent = (props) => {

 
  
  useEffect(()=>{
    
    props.fetchDishes();
  },[])


  const Homepage = () => {
    console.log(props.dish.isLoading)
    return (
     
      <HomeComponent
        dish={props.dish.dishes.filter((dish) => dish.featured)[0]}
        dishesLoading={props.dish.isLoading}
        dishesErrMess={props.dish.erMsg}
        promotions={props.promotions.filter((promo) => promo.featured)[0]}
        leaders={props.leaders.filter((leader) => leader.featured)[0]}
      />
    );
  };

  const DishWithId = ({ match }) => {
    // console.log(match.params.id);
    return (
      <DishdetailComponent
        dish={
          props.dish.dishes.filter(
            (dish) => dish.id === parseInt(match.params.id, 10)
          )[0]
        }
        isLoading={props.dish.isLoading}
        errMess={props.dish.erMsg}
        comments={props.comments.filter(
          (com) => com.dishId === parseInt(match.params.id, 10)
        )}
        addComment={props.addComment}
      />
      // <DishdetailComponent dish={dish} comments={comments}/>
    );
  };

  const aboutPage = () => {
    return <AboutComponent leaders={props.leaders} />;
  };
  return (
    <div>
      <HeaderComponent />
      <Switch>
        <Route path="/home" component={Homepage} />
        <Route
          exact
          path="/menu"
          component={() => <MenuComponent dish={props.dish} />}
        />
        <Route exact path="/contact" component={() => <ContactComponent />} />
        <Route path="/menu/:id" component={DishWithId} />
        <Route path="/about" component={aboutPage} />
        <Redirect to="/home" />
      </Switch>

      <FooterComponent />
    </div>
  );
};

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(MainComponent)
);
