import { LEADERS } from "../shared/leaders";

export const Leaders = (state={erMsg: null, leaders : []}, action) =>{

    switch(action.type){
        case ActionType.ADD_LEADERS:
            return {...state, isLoading : false, erMsg: null, leaders : action.payload}

      
        case ActionType.LEADERS_FAILED:
            return {...state, isLoading : false, erMsg: action.payload, leaders : []}

        default:
            return state
    }

}
