import React, { useEffect, useState } from "react";
import { Breadcrumb, BreadcrumbItem, Button, Label, Col,Row } from "reactstrap";

import { Control, LocalForm, Errors } from "react-redux-form";

import { Link } from "react-router-dom";
const ContactComponent = () => {
  // const [firstname, setfirstname] = useState("");
  // const [lastname, setlastname] = useState("");
  // const [telnum, settelnum] = useState("");
  // const [email, setemail] = useState("");
  // const [agree, setagree] = useState(false);
  // const [contactType, setcontactType] = useState("Tel.");
  // const [message, setmessage] = useState("");

  // function handelInputChange(event) {
  //   const target = event.target;
  //   const value = target.type === "checkbox" ? target.checked : target.value;
  //   const name = target.name;
  //   switch (name) {
  //     case "firstname":
  //       setfirstname(value);
  //       break;
  //     case "lastname":
  //       setlastname(value);
  //       break;
  //     case "telnum":
  //       settelnum(value);
  //       break;
  //     case "email":
  //       setemail(value);
  //       break;
  //     case "agree":
  //       setagree(value);
  //       break;
  //     case "contactType":
  //       setcontactType(value);
  //       break;
  //     case "message":
  //       setmessage(value);
  //       break;
  //     default:
  //       break;
  //   }
  // }

  function handelSubmit(values) {
    console.log(values);
    // validate(firstname, lastname)
    // event.preventDefault();
  }

  const errormsg = {
    firstnameError: "",
    lastnameError: "",
  };

  // function validate(firstname, lastname) {

  //   if (firstname == "")
  //     errormsg.firstnameError = "First name : This Can not be blanked";
  //   else if (firstname.length < 3)
  //     errormsg.firstnameError = "First name : Minimum length must be 3";

  //   if (lastname == "")
  //     errormsg.lastnameError = "Last name : This Can not be blanked";
  //   else if (lastname.length < 3)
  //     errormsg.lastnameError = "Last name : Minimum length must be 3";

  // }

  var msg = "";
  return (
    <div>
      <div className="container">
        <Breadcrumb>
          <BreadcrumbItem>
            <Link to="/home">Menu</Link>
          </BreadcrumbItem>
          <BreadcrumbItem active>Contact</BreadcrumbItem>
        </Breadcrumb>
        <div className="col-12">
          <h3>Contact</h3>
          <hr />
        </div>
        <div className="row row-content">
          <div className="col-12">
            <h3>Location Information</h3>
          </div>
          <div className="col-12 col-sm-4 offset-sm-1">
            <h5>Our Address</h5>
            <address>
              121, Clear Water Bay Road
              <br />
              Clear Water Bay, Kowloon
              <br />
              HONG KONG
              <br />
              <i className="fa fa-phone"></i>: +852 1234 5678
              <br />
              <i className="fa fa-fax"></i>: +852 8765 4321
              <br />
              <i className="fa fa-envelope"></i>:{" "}
              <a href="mailto:confusion@food.net">confusion@food.net</a>
            </address>
          </div>
          <div className="col-12 col-sm-6 offset-sm-1">
            <h5>Map of our Location</h5>
          </div>
          <div className="col-12 col-sm-11 offset-sm-1">
            <div className="btn-group" role="group">
              <a
                role="button"
                className="btn btn-primary"
                href="tel:+85212345678"
              >
                <i className="fa fa-phone"></i> Call
              </a>
              <a role="button" className="btn btn-info">
                <i className="fa fa-skype"></i> Skype
              </a>
              <a
                role="button"
                className="btn btn-success"
                href="mailto:confusion@food.net"
              >
                <i className="fa fa-envelope-o"></i> Email
              </a>
            </div>
          </div>
        </div>
        <div className="row row-content">
          <div className="col-12">
            <h3>Send Us Your Feedback</h3>
          </div>
          <div className="col-12 col-md-9">
            <LocalForm onSubmit={(values) => handelSubmit(values)}>
              <Row className="form-group">
                <Label htmlFor="firstname" md={2}>
                  First Name
                </Label>
                <Col md={10}>
                  <Control.text
                    model=".firstname"
                    className="form-control"
                    id="firstname"
                    name="firstname"
                    placeholder="Last Name"
                   
                  />
                </Col>
              </Row>

              <Row className="form-group">
                <Label htmlFor="lastname" md={2}>
                  Last Name
                </Label>
                <Col md={10}>
                  <Control.text
                    model=".lastname"
                    className="form-control"
                    id="lastname"
                    name="lastname"
                    placeholder="First Name"
                   
                  />
                </Col>
              </Row>

              <Row className="form-group">
                <Label htmlFor="telnum" md={2}>
                  Telephone
                </Label>
                <Col md={10}>
                  <Control.text
                    model=".telnum"
                    className="form-control"
                    id="telnum"
                    name="telnum"
                    placeholder="Telephone Number"
                  
                  />
                </Col>
              </Row>

              <Row className="form-group">
                <Label htmlFor="email" md={2}>
                  Email
                </Label>
                <Col md={10}>
                  <Control.text
                    model=".email"
                    className="form-control"
                    id="email"
                    name="email"
                    placeholder="Email"
                   
                  />
                </Col>
              </Row>

              <Row className="form-group">
                <Col md={{ size: 6, offset: 2 }}>
                  <div className="form-check">
                    <Label check>
                      <Control.checkbox
                        model=".agree"
                        name="agree"
                        className='from-check-input'
                      />{" "}
                      <strong>May We Contact You?</strong>
                    </Label>
                  </div>
                </Col>
                <Col md={{ size: 3, offset: 1 }}>
                  <Control.select
                    model=".contantType"
                    name="contactType"
                    className="form-control-select"
                  >
                    <option>Tel.</option>
                    <option>Email</option>
                  </Control.select>
                </Col>
              </Row>

              <Row className="form-group">
                <Label htmlFor="message" md={2}>
                  Message
                </Label>
                <Col md={10}>
                  <Control.textarea
                    model=".message"
                    id="message"
                    name="message"
                    placeholder="Message"
                    rows="5"
                    className="form-control"
                  />
                </Col>
              </Row>

              <Row className="form-group">
                <Col md={{ size: 10, offset: 2 }}>
                  <Button type="Submit" color="primary">
                    Send feedback
                  </Button>
                </Col>
              </Row>
            </LocalForm>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactComponent;
