import React, { useState, useEffect } from "react";
// import { DISHES } from "../shared/dishesh";
// import { COMMENTS } from "../shared/comments";
// import { LEADERS } from "../shared/leaders";
// import { PROMOTIONS } from "../shared/promotions";
import HomeComponent from "./HomeComponent";
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent";
import MenuComponent from "./MenuComponent";
import AboutComponent from "./AboutComponent";
import { Switch, Route, Redirect, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import ContactComponent from "./ContactComponent";
import DishdetailComponent from "./DishdetailComponent";






const MainComponent = () => {

  const mapStateToProps = state =>{
    return {
      dish : state.dish,
      comments : state.comments,
      promotions : state.promotions,
      leaders : state.leaders
    }
  
  }
  // const [dish, setdish] = useState(DISHES);
  // const [comments, setcomments] = useState(COMMENTS);
  // const [leaders, setleaders] = useState(LEADERS);
  // const [promotions, setpromotions] = useState(PROMOTIONS);

  useEffect(() => {
    // setdish(DISHES);
    // setcomments(COMMENTS);
    // setleaders(LEADERS);
    // setpromotions(PROMOTIONS);
  }, []);
  

  const Homepage = () => {
    return (
      <HomeComponent
        dish={this.props.dish.filter((dish) => dish.featured)[0]}
        promotions={this.props.promotions.filter((promo) => promo.featured)[0]}
        leaders={this.props.leaders.filter((leader) => leader.featured)[0]}
      />
    );
  };

  const DishWithId = ({match}) =>{
    // console.log(match.params.id);
    return (
      <DishdetailComponent dish={this.props.dish.filter((dish)=>dish.id===parseInt(match.params.id,10))[0]}
        comments={this.props.comments.filter((com)=>com.dishId===parseInt(match.params.id,10))[0]}
      />
      // <DishdetailComponent dish={dish} comments={comments}/>
    )
  }

  const aboutPage = () =>{
    return(
      <AboutComponent leaders={this.props.leaders}/>
    )
  }
  return (
    <div>
      <HeaderComponent />
      <Switch>
        <Route path="/home" component={Homepage} />
        <Route exact path="/menu" component={() => <MenuComponent dishes={this.state.dish} />}/>
        <Route exact path="/contact" component={() => <ContactComponent />} />
        <Route path="/menu/:id" component={DishWithId} />
        <Route path="/about" component={aboutPage} />
        <Redirect to="/home" />
      </Switch>

      <FooterComponent />
    </div>
  );
};

export default withRouter(connect(mapStateToProps)(MainComponent));
